  def test_nil_paramenters
    result = Booglan::PrettyNumber.new.perform(nil)
    assert_equal "This text has no pretty number", result
  end

  def test_no_pretty_numbers
    result = Booglan::PrettyNumber.new.perform('acc')
    assert_equal "This text has no pretty number", result
  end

  def test_has_more_than_one_pretty_number
    result = Booglan::PrettyNumber.new.perform(MANY_PRETTY_WORDS)
    assert_equal "This text has 140 pretty numbers", result
  end

  def test_has_one_pretty_number
    result = Booglan::PrettyNumber.new.perform(ONLY_ONE_PRETTY_NUMBER)
    assert_equal "This text has 1 pretty number", result
  end
end
